-- Name: match_material_chunks(extensions.vector, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.match_material_chunks(query_embedding extensions.vector, match_count integer DEFAULT 5) RETURNS TABLE(id uuid, chunk text, file_path text, similarity double precision)
    LANGUAGE sql STABLE
    AS $$
  select
    mc.id,
    mc.chunk,
    m.file_path,
    1 - (mc.embedding <=> query_embedding) as similarity
  from material_chunks mc
  join material m on mc.material_id = m.id
  order by mc.embedding <=> query_embedding
  limit match_count;
$$;


